import { useState, useEffect } from "react";
import axios from "axios";
import { Input, Box, Center, Heading, Button } from "@chakra-ui/react";
import { CarouselDemo } from "./CarouselDemo";
export const TakeABreak = () => {
  let [value, setValue] = useState("cats");
  let [title, setTitle] = useState("cats");

  let handleChange = (e) => {
      setTitle(e.target.value)
  }
  let gifInfo = {
    header: "Access-Control-Allow-Origin",
    giftitle: value,
  };
  useEffect(() => {
    axios.post("http://localhost:5000/gif", gifInfo).then((res) => {
      setTitle(res.data.data);
    });
  }, []);

  //   let submitGif = (e) => {
  //     let gifInfo = {
  //       header: "Access-Control-Allow-Origin",
  //       giftitle: value,
  //     };
  //     axios.post("http://localhost:5000/gif", gifInfo).then((res) => {
  //       setTitle(res.data.data);
  //       console.log(title)
  //     });
  //   };

  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="6xl" fontWeight="extrabold">
              Take a Break
            </Heading>
          </Center>
          <br />
          <br />
          <Input
            value={title}
            placeholder="Search for GIFs"
            variant="filled"
            onChange={handleChange}
          />
          <br />
          <br />
          {/* <Center>
            <Button onClick={submitGif}>Find GIFs</Button>
          </Center> */}
          <br />
          <br />
          <CarouselDemo title={title} />
        </Box>
      </Box>
    </div>
  );
};
