import { useState, useEffect } from "react";
import axios from "axios";
import { Box, Heading, Center, Input, Button, Text } from "@chakra-ui/react";
import { Card } from "./Card";
export const Chat = () => {
  const [message, setMessage] = useState("");
  const [allMessages, setAllMessages] = useState([]);
  let handleMessage = (e) => {
    setMessage(e.target.value);
  };
  let sendMessage = (e) => {
    let messageText = {
      header: "Access-Control-Allow-Origin",
      text: message,
    };
    allMessages.push(message);
    axios.post("http://localhost:5000/chat", messageText).then((res) => {
      allMessages.push(res.data.data);
    });
  };
  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="5xl" fontWeight="extrabold">
              AI Chat Therapy
            </Heading>
          </Center>
          <br />
          <br />
          <Input
            value={message}
            onChange={handleMessage}
            variant="filled"
            placeholder="Write a message to our AI Therapist"
          />
          <br />
          <br />
          <Center>
            <Button onClick={sendMessage}>Send</Button>
          </Center>
          <Card>
              <Center>
                  <Heading>Chat</Heading>
              </Center>
            {allMessages.map((message) => (
              <Text>{message}</Text>
            ))}
          </Card>
        </Box>
      </Box>
    </div>
  );
};
