import { useState, useEffect } from "react";
import axios from "axios";
import { Input, Box, Center, Heading, Button, Text } from "@chakra-ui/react";
import { CarouselDemo } from "./CarouselDemo";
import { GiphyFetch } from "@giphy/js-fetch-api";
import {
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  useDisclosure,
} from "@chakra-ui/react";
import ReactJkMusicPlayer from 'react-jinke-music-player'
import 'react-jinke-music-player/assets/index.css'

const giphyFetch = new GiphyFetch("T8n3iMoLUOguSsK56GgLJRYhh9lyrAWL");

export const TakeABreak = () => {
  let [value, setValue] = useState("cats");
  let updateValue = (e) => {
    setValue(e.target.value);
  };
  const { isOpen, onOpen, onClose } = useDisclosure();
  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="6xl" fontWeight="extrabold">
              Take a Break
            </Heading>
          </Center>
          <br />
          <br />
          <Center>
            <Button onClick={onOpen}>Explore GIFs</Button>
          </Center>
          <Modal
            isOpen={isOpen}
            onClose={onClose}
            motionPreset="slideInBottom"
            size={"xl"}
          >
            <ModalOverlay />
            <ModalContent>
              <ModalHeader>Find Awesome GIFs!</ModalHeader>
              <ModalCloseButton />
              <ModalBody>
                <CarouselDemo />
              </ModalBody>
              <ModalFooter>
                <Button colorScheme="blue" mr={3} onClick={onClose}>
                  Close
                </Button>
              </ModalFooter>
            </ModalContent>
          </Modal>
          <br />
          <br />
          <ReactJkMusicPlayer theme={"dark"} locale={"en_US"} mode={"full"}/>
        </Box>
      </Box>
    </div>
  );
};
