import firebase from "firebase";
import { useEffect, useState } from "react";
import axios from "axios";
const tsParticles = require("tsparticles");
import {
  Box,
  Heading,
  useColorModeValue,
  Text,
  Textarea,
  Button,
  Center,
  Input,
  Select,
  Flex,
  Image,
  Link,
  chakra,
  SimpleGrid,
  Grid,
  GridItem,
} from "@chakra-ui/react";
import Particles from "react-tsparticles";
// import { Card } from "./Card";
import { useHistory } from "react-router-dom";
import Card from "react-animated-3d-card";
import { Navbar } from "./Navbar";
export const Home = () => {
  let history = useHistory();

  let user_token = firebase.auth().currentUser.getIdToken();

  let user = {
    header: "Access-Control-Allow-Origin",
    token: user_token,
    email: firebase.auth().currentUser.email,
  };

  let signOut = () => {
    firebase.auth().signOut();
    history.push("/");
  };

  const goToChat = () => {
    history.push("/chat");
  };

  const goToWork = () => {
    history.push("/work");
  };

  const goToBreak = () => {
    history.push("/break");
  };

  useEffect(() => {
    axios.post("http://localhost:5000/home", user).then((res) => {
      console.log(res.data);
    });
  }, []);

  tsParticles.load("tsparticles", {
    fps_limit: 60,
    interactivity: {
      detect_on: "canvas",
      events: {
        onclick: { enable: true, mode: "push" },
        onhover: {
          enable: true,
          mode: "repulse",
          parallax: { enable: false, force: 60, smooth: 10 }
        },
        resize: true
      },
      modes: {
        bubble: { distance: 400, duration: 2, opacity: 0.8, size: 40, speed: 3 },
        grab: { distance: 400, line_linked: { opacity: 1 } },
        push: { particles_nb: 4 },
        remove: { particles_nb: 2 },
        repulse: { distance: 200, duration: 0.4 }
      }
    },
    particles: {
      color: { value: "#ffffff" },
      line_linked: {
        color: "#ffffff",
        distance: 150,
        enable: true,
        opacity: 0.4,
        warp: true,
        width: 1
      },
      move: {
        attract: { enable: false, rotateX: 600, rotateY: 1200 },
        bounce: false,
        direction: "none",
        enable: true,
        out_mode: "out",
        warp: true,
        random: false,
        speed: 2,
        straight: false
      },
      number: { density: { enable: true, value_area: 800 }, value: 80 },
      opacity: {
        anim: { enable: false, opacity_min: 0.1, speed: 1, sync: false },
        random: false,
        value: 0.5
      },
      shape: {
        character: {
          fill: false,
          font: "Verdana",
          style: "",
          value: "*",
          weight: "400"
        },
        image: {
          height: 100,
          replace_color: true,
          src: "images/github.svg",
          width: 100
        },
        polygon: { nb_sides: 5 },
        stroke: { color: "#000000", width: 0 },
        type: "circle"
      },
      size: {
        anim: { enable: false, size_min: 0.1, speed: 40, sync: false },
        random: true,
        value: 5
      }
    },
    polygon: {
      draw: { enable: false, lineColor: "#ffffff", lineWidth: 0.5 },
      move: { radius: 10 },
      scale: 1,
      type: "none",
      url: ""
    },
    retina_detect: true
  });

  return (
    <div id="tsparticles">
      <Navbar />
        <Box
          minH="100vh"
          py="12"
          px={{
            base: "4",
            lg: "8",
          }}
          bgGradient="linear(to-l, #7928CA, #FF0080)"
        >
          <Box maxW="md" mx="auto">
            <br />
            <br />
            <br />
            <Center>
              <Heading color="white" fontSize="6xl" fontWeight="extrabold">
                Dashboard
              </Heading>
            </Center>
            <br />
            <Center>
              <Box>
                <Card
                  style={{
                    background: "white",
                    width: "300px",
                    height: "300px",
                    cursor: "pointer",
                  }}
                >
                  <br />
                  <Text textAlign="center" fontSize="xl" fontWeight="bold">
                    Welcome
                    <Text
                      bgGradient="linear(to-l, #7928CA, #FF0080)"
                      bgClip="text"
                      fontWeight="bold"
                    >
                      {firebase.auth().currentUser.displayName}
                    </Text>
                  </Text>
                  <br />
                  <div align="center">
                    <img src={firebase.auth().currentUser.photoURL}></img>
                  </div>
                  <br />
                  <Center>
                    <Button
                      align="center"
                      color={"white"}
                      onClick={signOut}
                      bgGradient="linear(to-l, #7928CA, #FF0080)"
                      _hover={{
                        bgGradient: "linear(to-r, red.400,pink.400)",
                        boxShadow: "xl",
                      }}
                    >
                      Sign Out
                    </Button>
                  </Center>
                </Card>
              </Box>
            </Center>
            <br />
            <br />
            <br />
            <br />
            <SimpleGrid columns={[1, 3]} gap="200px">
              <Center>
                <Box as="button" onClick={goToWork} alignContent="center">
                  <Card
                    style={{
                      background: "linear-gradient(to right, #4296db, #86e4f7)",
                      width: "200px",
                      height: "200px",
                      cursor: "pointer",
                    }}
                  >
                    <br />
                    <br />
                    <br />
                    <Text
                      textAlign="center"
                      fontSize="3xl"
                      fontWeight="bold"
                      fontFamily="cursive"
                    >
                      Work
                    </Text>
                  </Card>
                </Box>
              </Center>
              <Center>
                <Box as="button" onClick={goToChat} alignContent="center">
                  <Card
                    style={{
                      background: "linear-gradient(to right, #3de2f5, #495df5)",
                      width: "200px",
                      height: "200px",
                      cursor: "pointer",
                    }}
                  >
                    <br />
                    <br />
                    <br />
                    <Text
                      textAlign="center"
                      fontSize="3xl"
                      fontWeight="bold"
                      fontFamily="cursive"
                    >
                      AI Therapy
                    </Text>
                  </Card>
                </Box>
              </Center>
              <Center>
                <Box as="button" onClick={goToBreak} alignContent="center">
                  <Card
                    style={{
                      background: "linear-gradient(to right, #3dbef5, #a14fff)",
                      width: "200px",
                      height: "200px",
                      cursor: "pointer",
                    }}
                  >
                    <br />
                    <br />
                    <br />
                    <Text
                      textAlign="center"
                      fontSize="3xl"
                      fontWeight="bold"
                      fontFamily="cursive"
                    >
                      Take a Break
                    </Text>
                  </Card>
                </Box>
              </Center>
            </SimpleGrid>
          </Box>
        </Box>
    </div>
  );
};
