import { useState, useEffect } from "react";
import axios from "axios"
import { Input, Box, Center, Heading, Button } from "@chakra-ui/react";
import { CarouselDemo } from "./CarouselDemo";
export const TakeABreak = () => {
  let [value, setValue] = useState("");
  let [title, setTitle] = useState("");
  const updateValue = (e) => {
      let title = e.target.value;
      setTitle(title);
  }
//   let gifInfo = {
//     header: "Access-Control-Allow-Origin", 
//     title: title
//   }
//   useEffect(() => {
//     axios.post("http://localhost:5000/gif", gifInfo).then((res) => {
//         setTitle(res.data)
//     });
//   }, []);

  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="6xl" fontWeight="extrabold">
              Take a Break
            </Heading>
          </Center>
          <br />
          <br />
          <Input
            value={title}
            focusBorderColor="blue.400"
            placeholder="Search for GIFs"
            variant="filled"
            onChange={updateValue}
          />
          <br />
          <br />
          {/* <Center>
            <Button onClick={updateValue}>Find GIFs</Button>
          </Center> */}
          <br />
          <br />

          <CarouselDemo title={title} />
        </Box>
      </Box>
    </div>
  );
};
