import React from "react";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import { Input, Button, Box, Heading, Progress } from "@chakra-ui/react";
import { CheckList } from "./CheckList";

export const Tasks = () => {
  let handleChange = (event) => setTask(event.target.value);
  let [task, setTask] = React.useState("");
  let [tasks, setTasks] = React.useState([]);

  let addTask = (e) => {
    setTask(task);
    setTasks([...tasks, task]);
  };

  return (
    <div>
      <Heading>Task List</Heading>
      <br />
      <Progress value={80} />
      <br />
      <Input
        placeholder="Enter a task"
        variant="filled"
        value={task}
        onChange={handleChange}
      />
      <br />
      <br />
      <Button colorScheme="facebook" onClick={addTask}>
        Submit
      </Button>
      <br />
      <br />
      {/* <CheckList tasks={tasks} /> */}
      <div>
        {tasks.map((task) => (
          <div>
            <Checkbox default IsUnchecked>
              {task}
            </Checkbox>
            <br />
          </div>
        ))}
      </div>
    </div>
  );
};
