import { useState, useEffect } from "react";
import axios from "axios";
import { Input, Box, Center, Heading, Button, Text } from "@chakra-ui/react";
import { CarouselDemo } from "./CarouselDemo";
import { GiphyFetch } from "@giphy/js-fetch-api";

const giphyFetch = new GiphyFetch("T8n3iMoLUOguSsK56GgLJRYhh9lyrAWL");

export const TakeABreak = () => {
  let [value, setValue] = useState("cats");
  let updateValue = (e) => {
    setValue(e.target.value);
  };
  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="6xl" fontWeight="extrabold">
              Take a Break
            </Heading>
          </Center>
          <br />
          <br />
          <Input
            value={value}
            placeholder="Search for GIFs"
            variant="filled"
            onChange={updateValue}
          />
          <CarouselDemo title="cats" />
          <Text>{value}</Text>
        </Box>
      </Box>
    </div>
  );
};
