import { useState, useEffect } from "react";
import axios from "axios";
import { Input, Box, Center, Heading, Button, Text } from "@chakra-ui/react";
import { CarouselDemo } from "./CarouselDemo";
export const TakeABreak = () => {
  //   let [title, setTitle] = useState("");
  //   let handleChange = (e) => {
  //     setTitle(e.target.value)
  //   }
  let [value, setValue] = useState("");
  let [title, setTitle] = useState("");
  let updateValue = (e) => {
    setValue(e.target.value);
  };
  let gifTitle = {
    header: "Access-Control-Allow-Origin",
    giftitle: value,
  };
  useEffect(() => {
    axios.post("http://localhost:5000/gif", gifTitle).then((res) => {
      setTitle(res.data.data);
    });
  });
  return (
    <div>
      <Box
        minH="100vh"
        py="12"
        px={{
          base: "4",
          lg: "8",
        }}
        bgGradient="linear(to-l, #7928CA, #FF0080)"
      >
        <Box maxW="md" mx="auto">
          <Center>
            <Heading color="white" fontSize="6xl" fontWeight="extrabold">
              Take a Break
            </Heading>
          </Center>
          <br />
          <br />
          <Input
            value={value}
            placeholder="Search for GIFs"
            variant="filled"
            onChange={updateValue}
          />
          <CarouselDemo title={title} />
          <Text>{value}</Text>
        </Box>
      </Box>
    </div>
  );
};
