import React from "react";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import { Input, Button, Box, Heading } from "@chakra-ui/react";
import { CheckList } from "./CheckList";

export const Tasks = () => {
  let handleChange = (event) => setTask(event.target.value);
  let [task, setTask] = React.useState("");
  let [tasks, setTasks] = React.useState([]);

  let addTask = (e) => {
    setTask(task);
    setTasks([...tasks, task]);
  };

  return (
    <div>
      <Heading>Task List</Heading>
      <br />
      <Input placeholder="Enter a task" variant="filled" value={task} onChange={handleChange} />
      <br />
      <br />
      <Button onClick={addTask}>Submit</Button>
      <br />
      <br />
      <CheckList tasks={tasks} />
    </div>
  );
};
