import React, { useEffect, useRef, useState } from "react";
import { render } from "react-dom";
import { GiphyFetch } from "@giphy/js-fetch-api";
import { Carousel } from "@giphy/react-components";

const giphyFetch = new GiphyFetch("IRqb8ITKqGqMho0YJ5iFpP4Ickqdwmlp");

export const CarouselDemo = ({ title }) => {
  const fetchGifs = (offset) => giphyFetch.search(title, { offset });
  return <Carousel fetchGifs={fetchGifs} gifHeight={200} gutter={6} />;
};
